struct PRESParticipantSecurityForwarder {
    PRESParticipant_ReturnObjectFunction returnIdentityHandle;
    PRESParticipant_ReturnObjectFunction returnIdentityToken;
    PRESParticipant_ReturnObjectFunction returnPermissionsHandle;
    PRESParticipant_ReturnObjectFunction returnPermissionsToken;
    PRESParticipant_ReturnObjectFunction unregisterParticipant;
    PRESParticipant_LogFunction log;
    PRESParticipant_GetPreAuthenticatedRemoteParticipantSecurityStateFunction
        getPreAuthenticatedRemoteParticipantSecurityState;
    PRESParticipant_ReturnObjectFunction returnHandshakeHandle;
    PRESParticipant_GetAuthenticatedRemoteParticipantSecurityStateFunction
        getAuthenticatedRemoteParticipantSecurityState;
    PRESParticipant_ReturnObjectFunction returnSharedSecretHandle;

    PRESParticipant_SetLocalParticipantTrustedStateFunction
        setLocalParticipantTrustedState;
    PRESParticipant_VerifyRemoteParticipantTrustedStateFunction
        verifyRemoteParticipantTrustedState;

    PRESParticipant_GetMaxPrivateSignatureSizeFunction
        getMaxPrivateSignatureSize;
    PRESParticipant_PrivateSignFunction privateSign;
    PRESParticipant_VerifyPrivateSignatureFunction verifyPrivateSignature;

    PRESParticipant_GetEndpointAttributesFunction getEndpointAttributes;

    PRESParticipant_ReturnObjectFunction unregisterDataReader;
    PRESParticipant_ReturnObjectFunction unregisterDataWriter;
    PRESParticipant_CheckRemoteDataReaderFunction checkRemoteDataReader;
    PRESParticipant_CheckRemoteDataWriterFunction checkRemoteDataWriter;
    PRESParticipant_BeginHandshakeReplyFunction beginHandshakeReply;
    PRESParticipant_ProcessHandshakeFunction processHandshake;
    PRESParticipant_RegisterLocalEndpointFunction registerLocalDataReader;
    PRESParticipant_RegisterLocalEndpointFunction registerLocalDataWriter;
    PRESParticipant_RegisterMatchedRemoteEndpointFunction
        registerMatchedRemoteEndpoint;
    PRESParticipant_CheckMatchPermissionsFunction checkMatchPermissions;
    PRESParticipant_SetRemoteInterceptorStateFunction
        setRemoteParticipantInterceptorState;
    PRESParticipant_SetRemoteInterceptorStateFunction
        setRemoteDataWriterInterceptorState;
    PRESParticipant_SetRemoteInterceptorStateFunction
        setRemoteDataReaderInterceptorState;
    PRESParticipant_AssertLocalEndpointInterceptorStateFunction
        assertLocalEndpointInterceptorState;
    PRESParticipant_TransformOutgoingMetaDataFunction
        transformOutgoingDataWriterSubmessage;
    PRESParticipant_TransformOutgoingMetaDataFunction
        transformOutgoingDataReaderSubmessage;
    PRESParticipant_TransformOutgoingMetaDataFunction
        transformOutgoingRtpsMessage;
    PRESParticipant_TransformIncomingDataFunction
        transformIncomingRtpsMessage;
    PRESParticipant_PreprocessInterceptorSubmsgFunction
        preprocessInterceptorSubmsg;
    PRESParticipant_TransformIncomingDataFunction
        transformIncomingDataWriterSubmessage;
    PRESParticipant_TransformIncomingDataFunction
        transformIncomingDataReaderSubmessage;

    /*** serialized data forwarders ***/
    PRESParticipant_GetTransformedOutgoingSerializedSampleSizeFunction
        getTransformedOutgoingSerializedSampleSize;
    PRESParticipant_TransformOutgoingSerializedDataFunction
        transformOutgoingSerializedData;
    PRESParticipant_TransformIncomingSerializedDataFnc
        transformIncomingSerializedData;

    /*** persistent crypto tokens forwarders ***/
    PRESParticipant_SetLocalInterceptorStateFunction
        setLocalDataWriterInterceptorState;
    PRESParticipant_TransformInterceptorStateFunction
        transformOutgoingLocalDataWriterInterceptorState;
    PRESParticipant_TransformInterceptorStateFunction
        transformIncomingLocalDataWriterInterceptorState;
};

